mapper.select:
select c.id, u.name as owner, c.name, c.website, c.phone, c.createBy, c.createTime, c.editBy, c.editTime, c.contactSummary, c.nextContactTime, c.description, c.address
from tbl_customer c join tbl_user u on c.owner=u.id
where c.id=#{id}

mapper.update:
update tbl_customer set
owner=#{owner}, name=#{name}, website=#{website}, phone=#{phone}, editBy=#{editBy}, editTime=#{editTime}, contactSummary=#{contactSummary}, nextContactTime=#{nextContactTime}, description=#{description}, address=#{address}
where id=#{id}

mapper.delete:
delete from tbl_customer

where id in


    #{id}

mapper.insert:
insert into tbl_customer(
id, owner, name, website, phone, createBy, createTime, contactSummary, nextContactTime, description, address
)
values(
#{id}, #{owner}, #{name}, #{website}, #{phone}, #{createBy}, #{createTime}, #{contactSummary}, #{nextContactTime}, #{description}, #{address}
)
